import React, { useEffect, useState } from "react";
import "./App.css";

const backendUrl = "http://127.0.0.1:5000";

function App() {
  const [csvs, setCsvs] = useState([]);
  const [organ, setOrgan] = useState("");
  const [data, setData] = useState([]);
  const [prediction, setPrediction] = useState(null);
  const [ragExplanation, setRagExplanation] = useState("");

  // Load CSV list
  useEffect(() => {
    fetch(`${backendUrl}/api/csvs`)
      .then(res => res.json())
      .then(setCsvs)
      .catch(console.error);
  }, []);

  // Fetch CSV data
  const fetchData = () => {
    if (!organ) return alert("Select an organ");
    fetch(`${backendUrl}/api/data?organ=${organ}`)
      .then(res => res.json())
      .then(setData)
      .catch(console.error);
  };

  // Fetch RAG explanation
  const getRagExplanation = (inputData) => {
    fetch(`${backendUrl}/rag/explain`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(inputData)
    })
      .then(res => res.json())
      .then(data => setRagExplanation(data.rag_explanation))
      .catch(console.error);
  };

  // Prediction
  const getPrediction = () => {
    if (!organ) return alert("Select an organ");

    const inputData = {
      organ,
      age: 45,
      bp: 120,
      cholesterol: 180,
      diabetes: 0,
      patient_id: 101
    };

    fetch(`${backendUrl}/predict`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(inputData)
    })
      .then(res => res.json())
      .then(data => {
        setPrediction(data);
        getRagExplanation(inputData);
      })
      .catch(console.error);
  };

  return (
    <div className="App">
      <h1>PrognosisIQ Dashboard</h1>

      <select onChange={e => setOrgan(e.target.value)}>
        <option value="">Select Organ</option>
        {csvs.map(f => (
          <option key={f} value={f}>{f}</option>
        ))}
      </select>

      <br /><br />
      <button onClick={fetchData}>Get CSV Data</button>

      <table>
        <thead>
          <tr>
            {data[0] && Object.keys(data[0]).map(k => (
              <th key={k}>{k}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((row, i) => (
            <tr key={i}>
              {Object.values(row).map((v, j) => (
                <td key={j}>{v}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      <br />
      <button onClick={getPrediction}>Get Prediction</button>

      {prediction && (
        <pre>{JSON.stringify(prediction, null, 2)}</pre>
      )}

      {ragExplanation && (
        <div style={{
          marginTop: "20px",
          padding: "15px",
          border: "1px solid #ccc",
          backgroundColor: "#f9f9f9"
        }}>
          <h3>ðŸ§  AI Explanation (RAG)</h3>
          <p>{ragExplanation}</p>
        </div>
      )}
    </div>
  );
}

export default App;
