import React, { useEffect, useState } from "react";
import "./App.css";

const backendUrl = "http://127.0.0.1:5000";

function App() {
  const [csvs, setCsvs] = useState([]);
  const [organ, setOrgan] = useState("");
  const [data, setData] = useState([]);
  const [prediction, setPrediction] = useState(null);

  useEffect(() => {
    fetch(`${backendUrl}/api/csvs`)
      .then(res => res.json())
      .then(setCsvs)
      .catch(console.error);
  }, []);

  const fetchData = () => {
    fetch(`${backendUrl}/api/data?organ=${organ}`)
      .then(res => res.json())
      .then(setData)
      .catch(console.error);
  };

  const getPrediction = () => {
    fetch(`${backendUrl}/predict`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        organ,
        age: 45,
        bp: 120,
        cholesterol: 180,
        diabetes: 0,
        patient_id: 101
      })
    })
      .then(res => res.json())
      .then(setPrediction)
      .catch(console.error);
  };

  return (
    <div className="App">
      <h1>PrognosisIQ Dashboard</h1>

      <select onChange={e => setOrgan(e.target.value)}>
        <option value="">Select Organ</option>
        {csvs.map(f => (
          <option key={f} value={f}>{f}</option>
        ))}
      </select>

      <button onClick={fetchData}>Get CSV Data</button>

      <table>
        <thead>
          <tr>
            {data[0] && Object.keys(data[0]).map(k => <th key={k}>{k}</th>)}
          </tr>
        </thead>
        <tbody>
          {data.map((row, i) => (
            <tr key={i}>
              {Object.values(row).map((v, j) => <td key={j}>{v}</td>)}
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={getPrediction}>Get Prediction</button>

      {prediction && (
        <pre>{JSON.stringify(prediction, null, 2)}</pre>
      )}
    </div>
  );
}

export default App;
